CREATE OR REPLACE FUNCTION obter_parametros_nf_quimio (ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


cd_natureza_operacao_ent_w	smallint;
cd_natureza_operacao_sai_w	smallint;
cd_operacao_nf_ent_w		smallint;
cd_operacao_nf_sai_w		smallint;
cd_condicao_pagamento_w		bigint;

BEGIN

select 	max(cd_natureza_operacao_ent),
	max(cd_natureza_operacao_sai),
	max(cd_operacao_nf_ent),
	max(cd_operacao_nf_sai),
	max(cd_condicao_pagamento)
into STRICT	cd_natureza_operacao_ent_w,
	cd_natureza_operacao_sai_w,
	cd_operacao_nf_ent_w,
	cd_operacao_nf_sai_w,
	cd_condicao_pagamento_w
from 	parametro_nf_quimio
where 	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (ie_opcao_p = 'NE') then
	return cd_natureza_operacao_ent_w;
elsif (ie_opcao_p = 'NS') then
	return cd_natureza_operacao_sai_w;
elsif (ie_opcao_p = 'OE') then
	return cd_operacao_nf_ent_w;
elsif (ie_opcao_p = 'OS') then
	return cd_operacao_nf_sai_w;
elsif (ie_opcao_p = 'CO') then
	return cd_condicao_pagamento_w;
end if;

end;
$body$
