CREATE OR REPLACE FUNCTION obter_parametro_ageint (cd_estabelecimento_p bigint, nm_parametro_p text, vl_default_p text) RETURNS varchar AS $body$
DECLARE


vl_parametro_w	varchar(10);
ds_comando_w		varchar(255);
ds_param_w		varchar(255);


BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_parametro_p IS NOT NULL AND nm_parametro_p::text <> '') then
	ds_comando_w 	:=	'select	nvl(max(' || nm_parametro_p || '),' || chr(39) || vl_default_p || chr(39) || ') ' ||
				'from		parametro_agenda_integrada ' ||
				'where		nvl(cd_estabelecimento,:cd_estabelecimento) = :cd_estabelecimento';
	ds_param_w	:=	'cd_estabelecimento=' || cd_estabelecimento_p;

	select	obter_select_concatenado_bv(ds_comando_w, ds_param_w, null)
	into STRICT	vl_parametro_w
	;
end if;

return vl_parametro_w;

end;
