CREATE OR REPLACE FUNCTION obter_parametro_funcao_padrao ( cd_funcao_p bigint, nr_seq_param_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

/* Utilizado no componente UEmail */

ds_parametro_w	varchar(255);
-- igual a function 'OBTER_PARAMETRO_FUNCAO' com a diferen√ßa que esta busca o valor padrao do parametro caso o usuario nao exista.
BEGIN

begin

select	vl_parametro
into STRICT	ds_parametro_w
from	funcao_param_usuario
where	cd_funcao = cd_funcao_p
and	nr_sequencia = nr_seq_param_p
and	upper(nm_usuario_param) = upper(nm_usuario_p)
and	coalesce(cd_estabelecimento,wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;
exception
	when others then

	if (coalesce(ds_parametro_w::text, '') = '') then
		begin

		select	vl_parametro
		into STRICT	ds_parametro_w
		from	funcao_param_funcao
		where	cd_funcao	= cd_funcao_p
		and	nr_seq_param	= nr_seq_param_p
		and	cd_funcao_config = wheb_usuario_pck.get_cd_funcao;

		exception
		when others then
			ds_parametro_w := null;
		end;
	end if;
	if (coalesce(ds_parametro_w::text, '') = '') then
		begin
		select	vl_parametro_padrao
		into STRICT	ds_parametro_w
		from	funcao_parametro
		where	cd_funcao = cd_funcao_p
		and		nr_sequencia = nr_seq_param_p;

		exception
		when others then
			ds_parametro_w := null;
		end;
	end if;

end;

return	ds_parametro_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
