CREATE OR REPLACE FUNCTION obter_param_perfil_funcao ( cd_perfil_p bigint, cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000) := '0';
nr_sequencia_w		bigint;
qt_perfil_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	funcao_parametro a
where	a.cd_funcao = cd_funcao_p;

c02 CURSOR FOR
SELECT	count(*)
from	funcao_param_perfil b
where	b.cd_funcao = cd_funcao_p
and	b.cd_perfil = cd_perfil_p
and	b.nr_sequencia = nr_sequencia_w;


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		qt_perfil_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (coalesce(qt_perfil_w,0) > 0) then
			begin
			if (coalesce(ds_retorno_w,'0') = '0') then
				ds_retorno_w := to_char(nr_sequencia_w);
			else
				ds_retorno_w := ds_retorno_w || ',' || to_char(nr_sequencia_w);
			end if;
			end;
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
