CREATE OR REPLACE FUNCTION obter_param_prescr_proc_int ( nr_seq_proc_interno_p bigint, cd_setor_atendimento_p bigint, ie_opcao_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

/*
I - Intervalo
Q - Quantidade
H - HorÃ¡rios
*/
ds_retorno_w		varchar(50) := null;
cd_intervalo_w		varchar(7);
qt_procedimento_w	double precision;
ds_horarios_w		varchar(255);
qt_regra_w		bigint;

c01 CURSOR FOR
	SELECT	qt_procedimento,
		cd_intervalo,
		ds_horarios
	from	proc_interno_prescr
	where	nr_seq_proc_interno						= nr_seq_proc_interno_p
	and		((coalesce(cd_convenio::text, '') = '') or (cd_convenio = obter_convenio_atendimento(nr_atendimento_p)))
	and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0))	= coalesce(cd_setor_atendimento_p,0)
	order by nr_sequencia;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	proc_interno_prescr
where	nr_seq_proc_interno						= nr_seq_proc_interno_p
and		((coalesce(cd_convenio::text, '') = '') or (cd_convenio = obter_convenio_atendimento(nr_atendimento_p)))
and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0))	= coalesce(cd_setor_atendimento_p,0);

if (qt_regra_w > 0) then
	open c01;
	loop
	fetch c01 into
		qt_procedimento_w,
		cd_intervalo_w,
		ds_horarios_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ie_opcao_p = 'I') then
			ds_retorno_w	:= cd_intervalo_w;
		elsif (ie_opcao_p = 'Q') then
			ds_retorno_w	:= qt_procedimento_w;
		elsif (ie_opcao_p = 'H') then
			ds_retorno_w	:= ds_horarios_w;
		else
			ds_retorno_w	:= '';
		end if;
		end;
	end loop;
	close c01;
end if;

return ds_retorno_w;

end;
$body$
