CREATE OR REPLACE FUNCTION obter_param_proced_prescr ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_setor_atendimento_p bigint, cd_perfil_p bigint, ie_opcao_p text, nr_seq_proc_interno_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE

/*
I - Intervalo
*/
ds_retorno_w		varchar(50) := null;
cd_intervalo_w		varchar(7);
cd_grupo_proc_w			bigint;
cd_especialidade_w		bigint;
cd_area_procedimento_w		bigint;

c01 CURSOR FOR
	SELECT	cd_intervalo
	from	regra_proced_prescr
	where	coalesce(cd_grupo_proc,coalesce(cd_grupo_proc_w,0))			= coalesce(cd_grupo_proc_w,0)
	and	coalesce(cd_especialidade, coalesce(cd_especialidade_w,0))		= coalesce(cd_especialidade_w,0)
	and	coalesce(cd_area_procedimento, coalesce(cd_area_procedimento_w,0))	= coalesce(cd_area_procedimento_w,0)
	and	coalesce(cd_procedimento,coalesce(cd_procedimento_p,0))			= coalesce(cd_procedimento_p,0)
	and	coalesce(cd_perfil, coalesce(cd_perfil_p,0))				= coalesce(cd_perfil_p,0)
	and	coalesce(nr_seq_proc_interno, coalesce(nr_seq_proc_interno_p,0))		= coalesce(nr_seq_proc_interno_p,0)
	and	coalesce(nr_seq_exame, coalesce(nr_seq_exame_p,0))			= coalesce(nr_seq_exame_p,0)
	and	((coalesce(cd_procedimento::text, '') = '') or (ie_origem_proced	= ie_origem_proced_p))
	and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0))	= coalesce(cd_setor_atendimento_p,0)
	order by nr_sequencia;


BEGIN

select	max(cd_grupo_proc),
	max(cd_especialidade),
	max(cd_area_procedimento)
into STRICT	cd_grupo_proc_w,
	cd_especialidade_w,
	cd_area_procedimento_w
from	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

open c01;
loop
fetch c01 into
	cd_intervalo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (ie_opcao_p = 'I') then
		ds_retorno_w	:= cd_intervalo_w;
	end if;
end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
