SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_param_usu_funcao ( nm_usuario_p text, cd_funcao_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter os parâmetros configurados para o usuário.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(4000) := '0';
nr_sequencia_w		bigint;
vl_parametro_w		bigint;

c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	funcao_parametro a
	where	a.cd_funcao = cd_funcao_p;

c02 CURSOR FOR
	SELECT	count(*)
	from	funcao_param_usuario b
	where	b.cd_funcao = cd_funcao_p
	and	b.nr_sequencia = nr_sequencia_w
	and	b.nm_usuario_param = nm_usuario_p;


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		vl_parametro_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (vl_parametro_w > 0) then
			begin
			if (coalesce(ds_retorno_w,'0') = '0') then
				ds_retorno_w := to_char(nr_sequencia_w);
			else
				ds_retorno_w := substr(ds_retorno_w || ',' || to_char(nr_sequencia_w),1,4000);
			end if;
			end;
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_param_usu_funcao ( nm_usuario_p text, cd_funcao_p bigint) FROM PUBLIC;

