SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_parcela_cheque_cr ( nr_cheque_p text, nr_seq_caixa_rec_p bigint, ie_tipo_dado_p text ) RETURNS varchar AS $body$
DECLARE


/* ie_tipo_dado_p:
	'P' = Numero da parcela
	'T' = Quantidade de parcelas
	'PT' = P/T
*/
nr_cheque_w		varchar(20);
nr_parcela_w		bigint;
nr_parcelas_w		bigint;
ds_retorno_w		varchar(10);

c01 CURSOR FOR
	SELECT	nr_cheque
	from	cheque_cr
	where	nr_seq_caixa_rec = nr_seq_caixa_rec_p
	order	by coalesce(dt_vencimento_atual,dt_vencimento);


BEGIN

if (nr_seq_caixa_rec_p > 0) then
	nr_parcela_w := 1;
	open c01;
	loop
	fetch c01 into
		nr_cheque_w;
	exit when nr_cheque_w = nr_cheque_p;
		nr_parcela_w := nr_parcela_w + 1;
	end loop;
	close c01;

	select	count(nr_cheque)
	into STRICT	nr_parcelas_w
	from	cheque_cr
	where	nr_seq_caixa_rec = nr_seq_caixa_rec_p;
	
	if (ie_tipo_dado_p = 'P') then
		ds_retorno_w := nr_parcela_w;
	elsif (ie_tipo_dado_p = 'T') then
		ds_retorno_w := nr_parcelas_w;
	else
		ds_retorno_w := nr_parcela_w || '/' || nr_parcelas_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
