CREATE OR REPLACE FUNCTION obter_parcela_tit_rec ( nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


qt_parcela_w	bigint	:= 1;
qt_reg_w	bigint	:= 0;
ds_parcela_w	varchar(10);
nr_tit_orig_w	titulo_receber.nr_titulo%type;
nr_titulo_w	titulo_receber.nr_titulo%type;

C01 CURSOR FOR
SELECT	b.nr_titulo_dest
from	titulo_receber a,
	titulo_receber_desdob b
where	a.nr_titulo	= b.nr_titulo
and	b.nr_titulo	= nr_tit_orig_w
order by b.dt_vencimento;


BEGIN

select	max(a.nr_titulo)
into STRICT	nr_tit_orig_w
from	titulo_receber a,
	titulo_receber_desdob b
where	a.nr_titulo		= b.nr_titulo
and	b.nr_titulo_dest	= nr_titulo_p;

open C01;
loop
fetch C01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_reg_w	:= qt_reg_w + 1;

	if (nr_titulo_w = nr_titulo_p) then
		qt_parcela_w	:= qt_reg_w;
	end if;
	end;
end loop;
close C01;

ds_parcela_w	:= qt_parcela_w || '/' || qt_reg_w;

return	ds_parcela_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
