CREATE OR REPLACE FUNCTION obter_parentesco_resp_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_parentesco_w	bigint := 0;
ds_parentesco_w	varchar(40) := '';


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	coalesce(max(nr_seq_parentesco),0)
	into STRICT	nr_seq_parentesco_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (coalesce(nr_seq_parentesco_w,0) > 0) then
		select	ds_parentesco
		into STRICT	ds_parentesco_w
		from	grau_parentesco
		where	nr_sequencia = nr_seq_parentesco_w;
	end if;
end if;

return ds_parentesco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
