CREATE OR REPLACE FUNCTION obter_parte_nome_phillips_pf ( nm_inteiro_p text, ds_parte_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
nm_pessoa_w		varchar(100);

qt_pos_ini_ultnome_w 	bigint;
qt_pos_ini_penultnome_w	bigint := null;

ds_nome_w		varchar(100);
ds_sobre_w		varchar(100);

BEGIN

if (nm_inteiro_p IS NOT NULL AND nm_inteiro_p::text <> '') then

	nm_pessoa_w	:= trim(both nm_inteiro_p);

	for i in 1..length(nm_pessoa_w) loop
		begin
		if (substr(nm_pessoa_w,i,1) = ' ') then
			if (lower(trim(both substr(nm_pessoa_w,i,length(nm_pessoa_w)))) in ('neto','neta','filho','filha','j√∫nior','junior','sobrinho','sobrinha')) then
				qt_pos_ini_penultnome_w := qt_pos_ini_ultnome_w;
			end if;

			qt_pos_ini_ultnome_w 	:= i;

		end if;
		end;
	end loop;

	qt_pos_ini_ultnome_w := coalesce(qt_pos_ini_penultnome_w,qt_pos_ini_ultnome_w);

	ds_nome_w 		:= substr(nm_pessoa_w,1, qt_pos_ini_ultnome_w);
	qt_pos_ini_ultnome_w	:= qt_pos_ini_ultnome_w + 1;
	ds_sobre_w 		:= substr(nm_pessoa_w,qt_pos_ini_ultnome_w ,length(nm_pessoa_w));


	if (lower(ds_parte_p) = 'nome') then
		ds_retorno_w := trim(both ds_nome_w);
	elsif (lower(ds_parte_p) = 'sobrenome') then
		ds_retorno_w := trim(both ds_sobre_w);
	end	if;

end if;

return	substr(ds_retorno_w,1,100);

end	;
$body$
LANGUAGE PLPGSQL
