SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_parte_nome ( nm_inteiro_p text, ds_parte_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(100);
nm_pessoa_w	varchar(100);
pos1		integer;
pos2		integer;


BEGIN

nm_pessoa_w	:= trim(both nm_inteiro_p);
pos1		:= position(chr(32) in nm_pessoa_w);
pos2		:= Instr(nm_pessoa_w, chr(32),-1);

if (lower(ds_parte_p) = 'nome') then
	ds_retorno_w := substr(nm_pessoa_w, 1, pos1 - 1);
elsif (lower(ds_parte_p) = lower(obter_desc_expressao(325926))/*'sobrenome'*/
) then
	ds_retorno_w := substr(nm_pessoa_w, pos2 + 1, 100);
elsif (lower(ds_parte_p) = 'restonome')then
	ds_retorno_w := substr(nm_pessoa_w, pos1 + 1, 100);
end	if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
