CREATE OR REPLACE FUNCTION obter_participante_cirurgia ( nr_cirurgia_p bigint, ie_funcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(80);


BEGIN 
 
select	max(substr(obter_nome_pf(cd_pessoa_fisica),1,70)) 
into STRICT	ds_retorno_w 
from	cirurgia_participante 
where	nr_cirurgia	= nr_cirurgia_p 
and	ie_funcao 	= ie_funcao_p;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
