SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_path_info (NR_SEQUENCIA_p bigint, TIPO_P text default 'CD') RETURNS varchar AS $body$
DECLARE


cd_icd_w  varchar(100);
lista varchar(200) := ' ';
ds_icd_w varchar(4000) := ' ';

c1 CURSOR FOR
SELECT b.CD_DOENCA_CID
from PROTOCOLO_INTEGRADO a, PROTOCOLO_INTEGRADO_DOENCA b
where a.NR_SEQUENCIA = NR_SEQUENCIA_p
and b.NR_SEQ_PROTOCOLO_INTEGRADO = a.NR_SEQUENCIA;


BEGIN

for cd_icd_w in c1
loop
  if lista != ' ' then
    lista := lista || ', ';
  end if;
  if ds_icd_w != ' ' then
    ds_icd_w := ds_icd_w || ', ';
  end if;
  lista := lista || cd_icd_w.CD_DOENCA_CID;
  ds_icd_w := ds_icd_w || substr(obter_desc_cid(cd_icd_w.CD_DOENCA_CID),1,100);
end loop;
if (TIPO_P = 'DS') then
  return ds_icd_w;
else
  return lista;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
