CREATE OR REPLACE FUNCTION obter_pat_local_material (cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_local_w	bigint;


BEGIN

begin
/*Material só pode ter um responsável*/

select	max(c.nr_sequencia)
into STRICT	nr_seq_local_w
from	material_resp_matricial a,
	man_localizacao b,
	pat_local c
where	a.nr_seq_localizacao	= b.nr_sequencia
and	b.nr_sequencia		= c.nr_seq_local_manutencao
and	a.cd_material		= cd_material_p;
exception when others then
	nr_seq_local_w	:= null;
end;

return	nr_seq_local_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_pat_local_material (cd_material_p bigint) FROM PUBLIC;

