SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ped_resp_parecer ( nr_atendimento_p bigint, ie_tipo_parecer_p text) RETURNS varchar AS $body$
DECLARE

/* 
P - pedido 
R - resposta 
*/
 
 
ds_retorno_w		varchar(255);
nm_medico_w		varchar(255);
ds_especialidade_w	varchar(255);
qt_reg_w		bigint;

BEGIN
begin 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_tipo_parecer_p IS NOT NULL AND ie_tipo_parecer_p::text <> '') then 
	begin 
	select	substr(obter_nome_pf(max(a.cd_medico)),1,255) nm_medico, 
	   	substr(Obter_Nome_Especialidade(max(a.cd_especialidade)),1,255) ds_especialidade, 
		count(*) 
	into STRICT	nm_medico_w, 
		ds_especialidade_w, 
		qt_reg_w 
	from 	evolucao_paciente a 
	where 	a.nr_atendimento = 	nr_atendimento_p 
	and  	a.ie_evolucao_clinica = 'P';
	 
	 
	if (qt_reg_w	= 0) then 
		select	substr(obter_nome_pf(max(a.cd_medico)),1,255) nm_medico, 
			substr(Obter_Nome_Especialidade(max(a.cd_especialidade_dest)),1,255) ds_especialidade 
		into STRICT	nm_medico_w, 
		ds_especialidade_w 
		from	PARECER_MEDICO_REQ a 
		where	nr_atendimento = nr_atendimento_p 
		and	ie_situacao = 'A';
	end if;
	 
	 
	 
	if (ie_tipo_parecer_p = 'P') then 
		ds_retorno_w	:=	ds_especialidade_w;
		elsif (ie_tipo_parecer_p = 'R') then 
			ds_retorno_w	:= 	nm_medico_w;
	end if;
	 
	end;
end if;
		 
exception 
	when others then 
	null;
end;
					 
return 	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
