SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pendencia_guia_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_alta_w			timestamp;
cd_autorizacao_w		varchar(20);
cd_senha_w		varchar(20);
dt_inicio_w		timestamp;
dt_final_w		timestamp;
qt_dia_w			smallint;
qt_fora_vigencia_w		smallint;
dt_final_max_w		timestamp;
qt_proc_pend_w		bigint;
ds_retorno_w		varchar(255) := '';
cd_estabelecimento_w	smallint;
qt_mat_atend_paciente_w	bigint;
qt_proced_expirada_w	bigint;


c01 CURSOR FOR
SELECT	cd_autorizacao,
	cd_senha,
	dt_inicio_vigencia,
	coalesce(dt_fim_vigencia, coalesce(dt_alta_w, clock_timestamp())),
	coalesce(qt_dia_autorizado,0)
from	convenio b,
	autorizacao_convenio a
where	a.cd_convenio	= b.cd_convenio
and	a.nr_atendimento	= nr_atendimento_p
and	b.ie_tipo_convenio	<> 1

union

select	nr_doc_convenio,
	cd_senha,
	dt_inicio_vigencia,
	coalesce(dt_final_vigencia, coalesce(dt_alta_w, clock_timestamp())),
	0
from	convenio b,
	atend_categoria_convenio a
where	a.cd_convenio	= b.cd_convenio
and	a.nr_atendimento	= nr_atendimento_p
and	b.ie_tipo_convenio	<> 1;



BEGIN

select	dt_alta,
	cd_estabelecimento
into STRICT	dt_alta_w,
	cd_estabelecimento_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p;

select	CASE WHEN count(*)=0 THEN  ''  ELSE '33 ' END
into STRICT	ds_retorno_w
from	convenio b,
	atendimento_paciente c,
	atend_categoria_convenio a
where	a.cd_convenio	= b.cd_convenio
and	a.nr_atendimento	= c.nr_atendimento
and	a.nr_atendimento	= nr_atendimento_p
and	b.ie_tipo_convenio	in (2,5,6,7,8,9)
--and	b.ie_exige_senha_atend	= 'S'
and	obter_se_exige_guia_senha(a.cd_convenio, c.ie_tipo_atendimento, null, 'S', cd_estabelecimento_w, c.ie_carater_inter_sus, c.ie_clinica,null,null,null,null,a.cd_categoria, a.cd_Plano_Convenio, c.cd_procedencia)	= 'S'
and	coalesce(a.cd_senha::text, '') = '';

open c01;
loop
fetch c01 into
	cd_autorizacao_w,
	cd_senha_w,
	dt_inicio_w,
	dt_final_w,
	qt_dia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (dt_final_w > dt_final_max_w) then
		dt_final_max_w := dt_final_w;
	end if;

	select	count(*)
	into STRICT	qt_proced_expirada_w
	from	procedimento_paciente
	where	nr_atendimento	= nr_atendimento_p
	and	nr_doc_convenio	= cd_autorizacao_w
	and	dt_conta not between dt_inicio_w and dt_final_w;

	select	count(*)
	into STRICT	qt_mat_atend_paciente_w
	from	material_atend_paciente
	where	nr_atendimento	= nr_atendimento_p
	and	nr_doc_convenio	= cd_autorizacao_w
	and	dt_conta not between dt_inicio_w and dt_final_w;

	qt_proc_pend_w := (qt_proced_expirada_w + qt_mat_atend_paciente_w);

/*	os144325 - diego
	select	sum(qt_proced_expirada)
	into	qt_proc_pend_w
	from	(
		select	count(*) qt_proced_expirada
		from	procedimento_paciente
		where	nr_atendimento	= nr_atendimento_p
		and	nr_doc_convenio	= cd_autorizacao_w
		and	dt_conta not between dt_inicio_w and dt_final_w
		union all
		select	count(*)
		from	material_atend_paciente
		where	nr_atendimento	= nr_atendimento_p
		and	nr_doc_convenio	= cd_autorizacao_w
		and	dt_conta not between dt_inicio_w and dt_final_w);*/
	if (qt_proc_pend_w > 0) and (coalesce(position('300 ' in ds_retorno_w),0) = 0) then
		ds_retorno_w	:= ds_retorno_w || '300 ';
	end if;

end loop;
close c01;

if (coalesce(position('300 ' in ds_retorno_w), 0) = 0) and (coalesce(dt_alta_w::text, '') = '') and (dt_final_max_w < clock_timestamp()) then
	ds_retorno_w := '300 ';
end if;

select	count(*)
into STRICT	qt_proc_pend_w
from	convenio c,
	procedimento_paciente b,
	conta_paciente a
where	a.nr_atendimento	= nr_atendimento_p
and	a.nr_interno_conta	= b.nr_interno_conta
and	a.cd_convenio_parametro	= c.cd_convenio
and	c.ie_tipo_convenio	<> 1
and	b.cd_situacao_glosa	<> 0
and	a.ie_status_acerto	= 1
and	coalesce(b.nr_doc_convenio::text, '') = '';

if (qt_proc_pend_w > 0) then
	ds_retorno_w := ds_retorno_w || '302 ';
else
	select	count(*)
	into STRICT	qt_proc_pend_w
	from	convenio c,
		material_atend_paciente b,
		conta_paciente a
	where	a.nr_atendimento	= nr_atendimento_p
	  and	a.nr_interno_conta	= b.nr_interno_conta
	  and	a.cd_convenio_parametro	= c.cd_convenio
	  and	c.ie_tipo_convenio	<> 1
	  and	b.cd_situacao_glosa	<> 0
	  and	a.ie_status_acerto	= 1
	  and	coalesce(b.nr_doc_convenio::text, '') = '';

	if (qt_proc_pend_w > 0) then
		ds_retorno_w	:= ds_retorno_w || '302 ';
	end if;
end if;

/* fabio 13/02/2004 - inclui select abaixo*/

select	count(*)
into STRICT	qt_fora_vigencia_w
from	atend_categoria_convenio a,
	atendimento_paciente c
where	a.nr_atendimento	= c.nr_atendimento
and	a.nr_atendimento	= nr_atendimento_p
and	(a.dt_final_vigencia IS NOT NULL AND a.dt_final_vigencia::text <> '')
and	a.dt_final_vigencia	=
		(SELECT	max(b.dt_final_vigencia)
		from	atend_categoria_convenio b
		where	b.nr_atendimento = a.nr_atendimento)
and	a.dt_final_vigencia	< clock_timestamp()
and	coalesce(c.dt_alta::text, '') = '';

if (qt_fora_vigencia_w > 0) then
	ds_retorno_w	:= ds_retorno_w || '32 ';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_pendencia_guia_atend ( nr_atendimento_p bigint) FROM PUBLIC;

