CREATE OR REPLACE FUNCTION obter_pendencia_pedido ( nr_seq_agenda_pedido_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_tabela_pedido_w		varchar(50);
nr_seq_pedido_w		bigint;
ie_tipo_encaminhamento_w	varchar(3);
ds_retorno_w		varchar(255);


BEGIN 
 
if (nr_seq_agenda_pedido_p IS NOT NULL AND nr_seq_agenda_pedido_p::text <> '') then 
	select	nm_tabela_pedido, 
		nr_seq_pedido 
	into STRICT	nm_tabela_pedido_w, 
		nr_seq_pedido_w 
	from	agenda_lista_espera 
	where	nr_sequencia = nr_seq_agenda_pedido_p;
 
	if (nm_tabela_pedido_w = 'PEDIDO_EXAME_EXTERNO') then 
		ds_retorno_w := substr(obter_desc_expressao(316637),1,50);
	elsif (nm_tabela_pedido_w = 'ATEND_PROGRAMA_SAUDE') then 
		ds_retorno_w := substr(obter_desc_expressao(787139),1,50);
	elsif (nm_tabela_pedido_w = 'ATEND_ENCAMINHAMENTO') then 
		select	ie_tipo_encaminhamento 
		into STRICT	ie_tipo_encaminhamento_w 
		from	atend_encaminhamento 
		where	nr_sequencia = nr_seq_pedido_w;
 
		if (ie_tipo_encaminhamento_w = 'I') then 
			ds_retorno_w := substr(obter_desc_expressao(788734),1,50);
		elsif (ie_tipo_encaminhamento_w = 'E') then 
			ds_retorno_w := substr(obter_desc_expressao(788736),1,50);
		end if;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
