SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pendencia_validacao (nr_seq_pendencia_p bigint, ie_tipo_validacao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
qt_pre_validacao_w	integer;
qt_interno_w		integer;
qt_externo_w		integer;


BEGIN

select 	coalesce(sum(case when ie_validation_type  = 'P' then 1 else 0 end),0) qt_pre_validacao,
	coalesce(sum(case when ie_validation_type  = 'I' then 1 else 0 end),0) qt_interno,
	coalesce(sum(case when ie_validation_type  = 'E' then 1 else 0 end),0) qt_externo
into STRICT	qt_pre_validacao_w,
	qt_interno_w,
	qt_externo_w
from 	reg_validation_people
where 	nr_seq_ct_validation = nr_seq_pendencia_p;

if (ie_tipo_validacao_p = 'PV') then
	if (qt_pre_validacao_w > 0) then
		if (qt_externo_w = 0 and qt_interno_w = 0) then
			ie_retorno_w := 'S';
		end if;
	end if;
elsif (ie_tipo_validacao_p = 'PP') then
	if (qt_pre_validacao_w = 0) then
		ie_retorno_w := 'S';
	end if;
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
