SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pend_escala_assinatura ( nm_usuario_p text, nr_sequencia_p bigint, ie_escala_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1):= 'N';
qt_reg_w				bigint;


BEGIN

if (ie_escala_p IS NOT NULL AND ie_escala_p::text <> '') then

	select	count(*)
	into STRICT	qt_reg_w
	from	pep_item_Pendente
	where	nr_seq_escala = nr_sequencia_p
	and		ie_escala = ie_escala_p
	and		ie_tipo_pendencia = 'A'
	and		nm_usuario = nm_usuario_p;

	if (qt_reg_w	= 0) then
		ds_retorno_w := 'N';
	else
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
