CREATE OR REPLACE FUNCTION obter_penultima_internacao ( dt_inicial_p timestamp, dt_final_p timestamp, cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE

 
nr_atendimento_w	bigint;


BEGIN 
 
 
select	max(nr_atendimento) 
into STRICT	nr_atendimento_w 
from	atendimento_paciente 
where	cd_pessoa_fisica 	= cd_pessoa_fisica_p 
and	nr_atendimento 	< (	SELECT	max(nr_atendimento) 
				from	atendimento_paciente 
				where	cd_pessoa_fisica = cd_pessoa_fisica_p 
				and	dt_entrada between dt_inicial_p and dt_final_p 
				and	ie_tipo_atendimento = 1) 
and	ie_tipo_atendimento = 1;
 
return	nr_atendimento_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
