CREATE OR REPLACE FUNCTION obter_penultima_pontuacao_pews (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

  cd_pontuacao_w bigint;

BEGIN
 select num.qt_pontuacao into STRICT cd_pontuacao_w
    from (SELECT row_number() OVER () AS RN, main.*
    from (select dt_avaliacao, qt_pontuacao
  from escala_pews
  where nr_atendimento = nr_atendimento_p
  order by dt_avaliacao desc) main) num
  where num.RN = 2;
  return cd_pontuacao_w;
end;
