CREATE OR REPLACE FUNCTION obter_penultimo_atendimento (nr_atend_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_pen_atend_w	bigint;			
			 

BEGIN 
 
select	nr_atendimento 
into STRICT	nr_pen_atend_w 
from (SELECT a.nr_atendimento 
	from	atendimento_paciente a, 
		atendimento_paciente b 
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	and	a.nr_atendimento < b.nr_atendimento 
	and	b.nr_atendimento = nr_atend_p 
	order by 1 desc) alias0 LIMIT 1;
 
 
return	nr_pen_atend_w;
 
end;
