CREATE OR REPLACE FUNCTION obter_penultimo_preco_brasidic (cd_brasindice_p text, ie_tipo_preco_p text, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	double precision;


BEGIN
select	max(a.vl_preco_medicamento)
into STRICT	ds_retorno_w
from	brasindice_preco a
where	a.cd_tiss = cd_brasindice_p
and	a.ie_tipo_preco = coalesce(ie_tipo_preco_p,a.ie_tipo_preco)
and	a.dt_inicio_vigencia    =(	SELECT	max(x.dt_inicio_vigencia)
					from    brasindice_preco x
					where   x.cd_tiss       = a.cd_tiss
					and	x.ie_tipo_preco	= a.ie_tipo_preco
					and     x.dt_inicio_vigencia    < dt_referencia_p);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
