SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_penul_triagem ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w	triagem_classif_risco.ds_classificacao%type;
nr_seq_triagem_w	log_prioridade_atend.nr_seq_triagem%type;
return_w            triagem_classif_risco.ds_classificacao%type;


BEGIN
IF (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') THEN
        SELECT substr(obter_desc_triagem(a.nr_seq_triagem), 1, 60) ds_triagem, a.nr_seq_triagem
            INTO STRICT ds_classificacao_w, nr_seq_triagem_w
        FROM log_prioridade_atend a
        WHERE a.nr_atendimento = nr_atendimento_p
            AND a.nr_sequencia = (SELECT MAX(b.nr_sequencia)
                                    FROM log_prioridade_atend b
                                    WHERE b.nr_atendimento = a.nr_atendimento
                                    AND b.nr_sequencia < (SELECT MAX(c.nr_sequencia)
                                                            FROM log_prioridade_atend c
                                                            WHERE c.nr_atendimento = b.nr_atendimento)
                                  );

    IF (ie_opcao_p = 'DESC') THEN
        return_w := ds_classificacao_w;
    ELSIF (ie_opcao_p = 'SEQ') THEN
        return_w := coalesce(nr_seq_triagem_w, 999);
    END IF;
END IF;

RETURN return_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
