CREATE OR REPLACE FUNCTION obter_pep_dt (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(4000);
dt_start timestamp;
dt_end timestamp;
C01 CURSOR FOR
SELECT max((select	max(dt_inicio)
		from cpoe_procedimento
		where	nr_sequencia	= a.NR_SEQ_PROC_CPOE)),
max((select	max(dt_fim)
		from cpoe_procedimento
		where	nr_sequencia	= a.NR_SEQ_PROC_CPOE))ds_fim
from PEP_PAC_CI a where a.nr_atendimento=nr_atendimento_p
and a.IE_TIPO_CONSENTIMENTO='P'
and (a.NR_SEQ_PROC_CPOE IS NOT NULL AND a.NR_SEQ_PROC_CPOE::text <> '')
and   (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and coalesce(a.dt_inativacao::text, '') = ''
order by a.DT_ATUALIZACAO;



BEGIN

open c01;
loop
fetch c01 into
		dt_start,dt_end;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(dt_start::text, '') = '') then
		ds_retorno_w :=  dt_start;
	else
		ds_retorno_w := obter_desc_expressao(303324) || ': ' || dt_start;
        if (dt_end IS NOT NULL AND dt_end::text <> '') then
        ds_retorno_w :=ds_retorno_w || chr(10)||obter_desc_expressao(311024)|| ': ' || dt_end;
        end if;
	end if;
	end;
end loop;
close c01;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
