CREATE OR REPLACE FUNCTION obter_percent_ativ_os_proj ( NR_OS_STATUS_GERAL_P bigint, NR_SEQ_ATIV_EXEC_P bigint) RETURNS bigint AS $body$
DECLARE

 PR_RETORNO_W bigint := 0;

BEGIN
	BEGIN
    SELECT coalesce(MAX(ATP.PR_ATIVIDADE), 0)
      INTO STRICT PR_RETORNO_W
      FROM MAN_ORDEM_SERVICO OS,
           MAN_ORDEM_ATIV_PREV ATP
     WHERE OS.NR_SEQUENCIA       = ATP.NR_SEQ_ORDEM_SERV
       AND OS.NR_SEQUENCIA       = NR_OS_STATUS_GERAL_P
       AND ATP.NR_SEQ_ATIV_EXEC IN (NR_SEQ_ATIV_EXEC_P);

	EXCEPTION
		WHEN no_data_found THEN
			PR_RETORNO_W := 0;
		WHEN OTHERS THEN
			PR_RETORNO_W := 0;
	END;

  RETURN PR_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
