CREATE OR REPLACE FUNCTION obter_percent_estrut_desc ( cd_convenio_p bigint, cd_estrutura_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


pr_retorno_w    double precision    := 0;

c01 CURSOR FOR
	SELECT	coalesce(pr_max_desc,0)
	from	conv_regra_estrut_desc
	where	cd_convenio = cd_convenio_p
	and	cd_estrutura = cd_estrutura_p
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
	order by	coalesce(pr_max_desc,0),
		coalesce(cd_estabelecimento,0);


BEGIN

open c01;
loop
fetch c01 into
	pr_retorno_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	pr_retorno_w	:= pr_retorno_w;
	end;
end loop;
close c01;

return  pr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
