CREATE OR REPLACE FUNCTION obter_perc_func_idioma ( cd_funcao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_reg_nec_rev_w	bigint;
qt_reg_w		bigint;

BEGIN

select	sum(contador)
into STRICT	qt_reg_nec_rev_w
from (
	SELECT	count(*) contador
	from	dic_objeto_idioma
	where	cd_funcao = cd_funcao_p
	and	ie_necessita_revisao = 'N'
	
union

	SELECT	count(*) contador
	from	dic_objeto_filtro_idioma
	where	cd_funcao = cd_funcao_p
	and	ie_necessita_revisao = 'N'
	) alias3;

select	sum(contador)
into STRICT	qt_reg_w
from (
	SELECT	count(*) contador
	from	dic_objeto_idioma
	where	cd_funcao = cd_funcao_p
	
union

	SELECT	count(*) contador
	from	dic_objeto_filtro_idioma
	where	cd_funcao = cd_funcao_p
	) alias3;

return	round((qt_reg_nec_rev_w * 100) / qt_reg_w, 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
