SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_perc_tp_obj_idioma ( cd_funcao_p bigint, ie_tipo_objeto_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
qt_total_registros_w	bigint;
qt_total_registros2_w	bigint;
qt_reg_filtro_w		bigint;
qt_reg_filtro2_w	bigint;
ds_tipo_objeto_p	varchar(50);
ds_tipo_objeto2_p	varchar(50) := null;

BEGIN

if (ie_tipo_objeto_p = 'WJPUM') then
	ds_tipo_objeto_p := 'MI';
elsif (ie_tipo_objeto_p = 'WJP') then
	ds_tipo_objeto_p := 'P';
elsif (ie_tipo_objeto_p = 'TXT') then
	ds_tipo_objeto_p := 'T';
elsif (ie_tipo_objeto_p = 'WF') then
	ds_tipo_objeto_p := 'WF';
elsif (ie_tipo_objeto_p = 'WCP') then
	ds_tipo_objeto_p := 'C';
elsif (ie_tipo_objeto_p = 'WSCB') then
	ds_tipo_objeto_p := 'CWSCB';
	ds_tipo_objeto2_p := 'CEWSCB';
end if;

select	count(*)
into STRICT	qt_total_registros_w
from	dic_objeto_idioma
where	cd_funcao = cd_funcao_p
and	((ie_tipo_objeto = ds_tipo_objeto_p) or (ie_tipo_objeto = ds_tipo_objeto2_p));

select	count(*)
into STRICT	qt_reg_filtro_w
from	dic_objeto_idioma
where	cd_funcao = cd_funcao_p
and	((ie_tipo_objeto = ds_tipo_objeto_p) or (ie_tipo_objeto = ds_tipo_objeto2_p))
and	ie_necessita_revisao = 'N';

if (ds_tipo_objeto_p = 'WF') then
	select	count(*)
	into STRICT	qt_total_registros2_w
	from	dic_objeto_filtro_idioma
	where	cd_funcao = cd_funcao_p;

	select	count(*)
	into STRICT	qt_reg_filtro2_w
	from	dic_objeto_filtro_idioma
	where	cd_funcao = cd_funcao_p
	and	ie_necessita_revisao = 'N';

	qt_total_registros_w := qt_total_registros_w + qt_total_registros2_w;
	qt_reg_filtro_w := qt_reg_filtro_w + qt_reg_filtro2_w;
elsif (ds_tipo_objeto_p = 'C') then
	select	count(*)
	into STRICT	qt_total_registros2_w
	from	dic_objeto_idioma
	where	cd_funcao = cd_funcao_p
	and	ie_tipo_objeto = 'AC';

	select	count(*)
	into STRICT	qt_reg_filtro2_w
	from	dic_objeto_idioma
	where	cd_funcao = cd_funcao_p
	and	ie_tipo_objeto = 'AC'
	and	ie_necessita_revisao = 'N';

	qt_total_registros_w := qt_total_registros_w + qt_total_registros2_w;
	qt_reg_filtro_w := qt_reg_filtro_w + qt_reg_filtro2_w;
end if;

if (qt_total_registros_w = 0) then
	qt_total_registros_w := 1;
end if;

if (qt_reg_filtro_w = 0) then
	qt_reg_filtro_w := 1;
end if;

return	round((qt_reg_filtro_w * 100) / qt_total_registros_w, 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
