CREATE OR REPLACE FUNCTION obter_perda_conta_paciente ( nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_retorno_w		bigint;


BEGIN

if (coalesce(nr_interno_conta_p, 0) <> 0) then

	select	max(nr_seq_perda)
	into STRICT	nr_seq_retorno_w
	from	pre_fatur_perda_conta
	where	nr_interno_conta	= nr_interno_conta_p;

end if;

return	nr_seq_retorno_w;

end;
$body$
