CREATE OR REPLACE FUNCTION obter_perda_conta_pac_baixa ( nr_interno_conta_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_retorno_w		bigint;


BEGIN

if (coalesce(nr_interno_conta_p, 0) <> 0) then
	select	max(a.nr_seq_perda)
	into STRICT	nr_seq_retorno_w
	from	pre_fatur_perda b,
		pre_fatur_perda_conta a
	where	a.nr_interno_conta	= nr_interno_conta_p
	and	a.nr_seq_perda	= b.nr_sequencia
	and	(b.dt_baixa IS NOT NULL AND b.dt_baixa::text <> '');

end if;

return	nr_seq_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
