CREATE OR REPLACE FUNCTION obter_perfil_nutricao_pf ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_perfil_pf_w	bigint;
ds_retorno_w		varchar(80);



BEGIN

select	coalesce(max(nr_seq_nut_perfil), 0)
into STRICT	nr_seq_perfil_pf_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (ie_opcao_p = 'C') then
	ds_retorno_w := nr_seq_perfil_pf_w;
else
	select	ds_perfil
	into STRICT	ds_retorno_w
	from	nut_perfil
	where	nr_sequencia = nr_seq_perfil_pf_w;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
