CREATE OR REPLACE FUNCTION obter_perfil_usua_aval_com_int (nr_seq_avaliacao_p bigint, cd_estabelecimento_p bigint, opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_perfil_w		integer;
ds_retorno_perfil_w	varchar(4000);
ds_retorno_usuario_w	varchar(4000);
nm_usuario_w		varchar(60);



C01 CURSOR FOR
	SELECT	distinct(cd_perfil) cd_perfil_comunicacao
	FROM	med_perfil_avaliacao
	WHERE	nr_seq_tipo_aval	= nr_seq_avaliacao_p
	AND	coalesce(cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p
	and	coalesce(IE_ENVIAR_CI,'S') = 'S'
	ORDER BY coalesce(cd_perfil_comunicacao,0);

C02 CURSOR FOR
	SELECT 	DISTINCT(SUBSTR(OBTER_USUARIO_PESSOA(CD_MEDICO),1,255)) nm_usuario
	FROM	MED_MEDICO_AVALIACAO
	WHERE	nr_seq_tipo_aval = nr_seq_avaliacao_p
	AND	(OBTER_USUARIO_PESSOA(CD_MEDICO) IS NOT NULL AND (OBTER_USUARIO_PESSOA(CD_MEDICO))::text <> '')
	and	coalesce(IE_ENVIAR_CI,'S') = 'S'
	order by coalesce(nm_usuario,0);


BEGIN

open c01;
loop
fetch c01 into
		cd_perfil_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(ds_retorno_perfil_w::text, '') = '') then
		ds_retorno_perfil_w := to_char(cd_perfil_w);
	else

	ds_retorno_perfil_w	:= ds_retorno_perfil_w||','||to_char(cd_perfil_w);
	end if;

	end;
end loop;
close c01;

open C02;
loop
fetch C02 into
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	if (coalesce(ds_retorno_usuario_w::text, '') = '') then
		ds_retorno_usuario_w := nm_usuario_w;
	else
	ds_retorno_usuario_w	:= ds_retorno_usuario_w||','||nm_usuario_w;
	end if;

	end;
end loop;
close C02;

if (opcao_p = 'P') then
	return ds_retorno_perfil_w;
elsif (opcao_p = 'U') then
	return ds_retorno_usuario_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
