CREATE OR REPLACE FUNCTION obter_periodicidade_agravo ( cd_diag_atend_p text) RETURNS varchar AS $body$
DECLARE

ds_periodicidade_w		varchar(255);


BEGIN

if (cd_diag_atend_p IS NOT NULL AND cd_diag_atend_p::text <> '') then
	select max(substr(obter_valor_dominio(8350,ie_periodicidade),1,255))
	into STRICT ds_periodicidade_w
	from cih_doenca_compulsoria
	where cd_doenca_cid = cd_diag_atend_p
	and   ie_situacao = 'A';
end if;

return	ds_periodicidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
