CREATE OR REPLACE FUNCTION obter_periodo_conta ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

dt_retorno_w	timestamp;
/*ie_opcao_p: 
PI = Período Inicial 
PF = Período Final 
*/
BEGIN 
 
if (ie_opcao_p = 'PI') then 
	begin 
	select	dt_periodo_inicial 
	into STRICT	dt_retorno_w 
	from	conta_paciente 
	where 	nr_interno_conta = nr_interno_conta_p;
	exception 
		when others then 
		dt_retorno_w := null;
		end;
elsif (ie_opcao_p = 'PF') then 
	begin 
	select	dt_periodo_final 
	into STRICT	dt_retorno_w 
	from	conta_paciente 
	where 	nr_interno_conta = nr_interno_conta_p;
	exception 
		when others then 
		dt_retorno_w := null;
		end;
 
end if;
 
return	dt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
