CREATE OR REPLACE FUNCTION obter_periodo_estoque ( dt_inicio_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dias_w		smallint;


BEGIN

select	/* + index (a EISESTO_I1) */
	count(distinct dt_referencia)
into STRICT	qt_dias_w
from	eis_estoque_v a
where	cd_estabelecimento = cd_estabelecimento_p
and	(cd_local_estoque IS NOT NULL AND cd_local_estoque::text <> '')
and	dt_referencia between dt_inicio_p and dt_final_p;

return	qt_dias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
