CREATE OR REPLACE FUNCTION obter_periodo_isolamento (nr_atendimento_w bigint, dt_inicio_p timestamp, dt_termino_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_isolamento_w		bigint;
dt_inicio_w		timestamp;
dt_termino_w		timestamp;
ds_retorno_w		varchar(1) := 'N';


BEGIN

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then
	dt_inicio_w	:= dt_inicio_p;
	dt_termino_w	:= trunc(dt_termino_p) + 86399/86400;

	select 	count(*)
	into STRICT	qt_isolamento_w
	from	motivo_isolamento_atend
	where	nr_atendimento = nr_atendimento_w
	and	((dt_inicio_isolamento  between dt_inicio_w and  dt_termino_w) or (dt_fim_isolamento 	between dt_inicio_w and  dt_termino_w));

	if (qt_isolamento_w > 0) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
