SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_periodo_precaucao (nr_atendimento_p bigint, dt_inicio_p timestamp, dt_termino_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_precaucao_w	bigint;
ds_retorno_w	varchar(1) := 'N';

dt_inicio_w	timestamp;
dt_termino_w	timestamp;


BEGIN

dt_inicio_w	:= dt_inicio_p;
dt_termino_w	:= trunc(dt_termino_p) + 86399/86400;

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select 	count(*)
	into STRICT	qt_precaucao_w
	from	atendimento_precaucao
	where	nr_atendimento = nr_atendimento_p
	and	((dt_inicio  between dt_inicio_w and  dt_termino_w) or (dt_termino between dt_inicio_w and  dt_termino_w));

	if (qt_precaucao_w > 0) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
