CREATE OR REPLACE FUNCTION obter_permicao_conv_medic ( cd_convenio_p bigint, nr_seq_prot_medic_mat_p bigint, cd_protocolo_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(1) := 'S';
qt_convenio_w	bigint;

C01 CURSOR FOR
	SELECT	coalesce(ie_permite_medic,'S')
