	from	protocolo_medic_mat_conv
	where   nr_seq_medicacao = nr_seq_prot_medic_mat_p
	and     cd_protocolo     = cd_protocolo_p
	and     nr_seq_material  = nr_seq_material_p
	and    coalesce(cd_convenio,cd_convenio_p)      = cd_convenio_p
	order by coalesce(cd_convenio,0);


BEGIN
select 	count(*)
into STRICT	qt_convenio_w
from   	protocolo_medic_mat_conv
where   nr_seq_medicacao = nr_seq_prot_medic_mat_p
and     cd_protocolo     = cd_protocolo_p
and     nr_seq_material  = nr_seq_material_p
and    coalesce(cd_convenio,cd_convenio_p)      = cd_convenio_p;

if (qt_convenio_w > 0) then
