CREATE OR REPLACE FUNCTION obter_permissao_ressuprimento (dt_liberacao_p timestamp, cd_pessoa_requisitante_p text) RETURNS varchar AS $body$
DECLARE

 
				 
ie_permite_w						varchar(1) := 'S';
ie_permite_alterar_liberada_w		varchar(2);
ie_permite_alterar_outro_req_w		varchar(2);
nm_usuario_w						varchar(25);
cd_pf_usuario_w						varchar(25);		
 
				 

BEGIN 
 
nm_usuario_w	:=	obter_usuario_ativo;
 
ie_permite_alterar_liberada_w 	:= coalesce(obter_valor_param_usuario(220, 3, obter_perfil_ativo, nm_usuario_w, obter_estabelecimento_ativo),'N');
ie_permite_alterar_outro_req_w 	:= coalesce(obter_valor_param_usuario(220, 2, obter_perfil_ativo, nm_usuario_w, obter_estabelecimento_ativo),'N');
cd_pf_usuario_w					:=	Obter_Pf_Usuario(nm_usuario_w,'C');
 
 
 
 
if (ie_permite_alterar_liberada_w = 'N') and (dt_liberacao_p IS NOT NULL AND dt_liberacao_p::text <> '') then 
	ie_permite_w	:=	'N';
end if;
 
if (ie_permite_alterar_outro_req_w = 'N') and (cd_pessoa_requisitante_p <> cd_pf_usuario_w) then 
	ie_permite_w	:=	'N';
end if;
 
 
return	ie_permite_w;
 
end;
$body$
