CREATE OR REPLACE FUNCTION obter_permite_alterar_ficha (nr_ficha_ocorrencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
nr_ficha_ocorrencia_w	bigint  := NULL;


BEGIN

select 	max(nr_ficha_ocorrencia)
into STRICT	nr_ficha_ocorrencia_w
from	cih_ficha_ocorrencia
where	nr_ficha_ocorrencia = nr_ficha_ocorrencia_p
and	coalesce(dt_encerramento::text, '') = ''
and	coalesce(dt_liberacao::text, '') = '';

if (nr_ficha_ocorrencia_w IS NOT NULL AND nr_ficha_ocorrencia_w::text <> '') then

	ds_retorno_w := 'S';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
