CREATE OR REPLACE FUNCTION obter_permite_dispensacao (nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


param683_w	double precision;
param684_w	double precision;
dt_horario_w timestamp;


BEGIN

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') and (nr_seq_horario_p > 0) then

	select 	max(dt_horario)
	into STRICT 	dt_horario_w
	from 	prescr_mat_hor
	where 	nr_sequencia 	= nr_seq_horario_p;

	if (dt_horario_w IS NOT NULL AND dt_horario_w::text <> '') then

		param683_w := obter_param_usuario(1113, 683, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, param683_w);
		param684_w := obter_param_usuario(1113, 684, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, param684_w);

	    if (clock_timestamp() >= (dt_horario_w - (param683_w * (1/24/60))) or (coalesce(param683_w, 0) = 0)) and
		   (clock_timestamp() <= (dt_horario_w + (param684_w * (1/24/60))) or (coalesce(param684_w, 0) = 0)) then
			return 'S';
		else
			return 'N';
		end if;

	else
		return 'S';
	end if;

else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
