CREATE OR REPLACE FUNCTION obter_permite_lanc_disp_pepo ( cd_material_p bigint, nr_seq_agente_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2);
ie_anestesista_w	varchar(2);
ie_cirurgiao_w		varchar(2);
ie_enfermagem_w		varchar(2);
ie_pediatra_w		varchar(2);
/*
ie_opcao_p:
A - Deve lançar para anestesita
C - Deve lançar para cirurgiao
E - Deve lançar para enfermagem
P - Deve lançar para pediatra
*/
BEGIN

if (cd_material_p > 0) then
	select	max(ie_anestesista),
		max(ie_cirurgiao),
		max(ie_enfermagem),
		max(ie_pediatra)
	into STRICT	ie_anestesista_w,
		ie_cirurgiao_w,
		ie_enfermagem_w,
		ie_pediatra_w
	from	agente_anest_material
	where	cd_material = cd_material_p
	and (nr_seq_agente_p = 0 or nr_seq_agente = nr_seq_agente_p);
end if;

if (ie_opcao_p = 'A') then
	ds_retorno_w	:= ie_anestesista_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w	:= ie_cirurgiao_w;
elsif (ie_opcao_p = 'E') then
	ds_retorno_w	:= ie_enfermagem_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w	:= ie_pediatra_w;
end if;

if (coalesce(ds_retorno_w::text, '') = '') then
	ds_retorno_w := 'S';
end if;
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
