CREATE OR REPLACE FUNCTION obter_permite_nova_etapa (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255)	:= null;


BEGIN

select	coalesce(max('N'), 'S')
into STRICT	ds_retorno_w
from	fatur_etapa b,
	conta_paciente_etapa a
where	a.nr_seq_etapa	= b.nr_sequencia
and	a.nr_interno_conta = nr_interno_conta_p
and	coalesce(a.dt_fim_etapa::text, '') = ''
and	coalesce(b.ie_situacao,'A') = 'A'
and	coalesce(b.ie_nova_etapa_manual, 'S') = 'N';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
