CREATE OR REPLACE FUNCTION obter_perm_acesso_visita ( nr_seq_Acesso_visita_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w		bigint;
ds_Retorno_w	varchar(10)	:= 'S';


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	regra_acesso_visita_perfil
where	nr_seq_acesso_visita	= nr_seq_acesso_visita_p
and		ie_Situacao				= 'A';

if (qt_regra_w	> 0) then
	select	count(*)
	into STRICT	qt_regra_w
	from	regra_acesso_visita_perfil
	where	nr_seq_acesso_visita	= nr_seq_acesso_visita_p
	and		ie_Situacao				= 'A'
	and		cd_perfil				= cd_perfil_p;

	if (qt_regra_w	= 0) then
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
