CREATE OR REPLACE FUNCTION obter_perm_env_privac ( cd_pessoa_fisica_p bigint, ie_tipo_consentimento_p text, ie_tipo_documento_p text default null, ie_tipo_complemento_pf_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_perm_envio_w pessoa_fisica_privac_item.ie_aceite%type;
				

BEGIN
	
	SELECT coalesce(Max(pfpi.ie_aceite),'S')
	  INTO STRICT ie_perm_envio_w
	  FROM pessoa_fisica_privac_item pfpi,
		   pessoa_fisica_privacidade pfp
	 WHERE pfpi.nr_seq_pf_privacidade = pfp.nr_sequencia
	   AND pfpi.ie_tipo_consentimento = ie_tipo_consentimento_p
	   AND (pfp.dt_liberacao IS NOT NULL AND pfp.dt_liberacao::text <> '')
	   AND coalesce(pfp.ie_situacao,'A') = 'A'
	   AND (pfpi.ie_tipo_documento = ie_tipo_documento_p or coalesce(ie_tipo_documento_p::text, '') = '')
	   AND (pfpi.ie_tipo_complemento_pf = ie_tipo_complemento_pf_p or coalesce(ie_tipo_complemento_pf_p::text, '') = '')
	   AND pfp.cd_pessoa_fisica = cd_pessoa_fisica_p;
	   
	   return ie_perm_envio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
