SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_perm_vis_comunic_cih ( cd_medico_p text, nr_sequencia_p bigint, nr_seq_tipo_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
ie_destinatario_w	varchar(5);
permissao_w		varchar(1);
cd_prescr_w		varchar(10);
cd_medico_w		varchar(10);
eh_medico_w		bigint;


BEGIN

select	ie_destinatario
into STRICT	ie_destinatario_w
from	tipo_comunicado_cih
where	nr_sequencia = nr_seq_tipo_p;

if (ie_destinatario_w = 'MCC') then
	select	max(ie_corpo_clinico)
	into STRICT	permissao_w
	from	medico
	where	cd_pessoa_fisica = cd_medico_p;

elsif (ie_destinatario_w = 'MP') then
	select	max(cd_prescritor)
	into STRICT	cd_prescr_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	if (cd_prescr_w = cd_medico_p) then
		permissao_w := 'S';
	else
		permissao_w := 'N';
	end if;
elsif (ie_destinatario_w = 'QM') then
	select count(*)
	into STRICT eh_medico_w
	from medico
	where cd_pessoa_fisica = cd_medico_p;

	if (eh_medico_w = 0) then
	permissao_w := 'N';
	else
	permissao_w := 'S';
	end if;
end if;

ds_retorno_w 	:= permissao_w;

select	cd_pessoa_destino
into STRICT	cd_medico_w
from	prescr_mat_comunic_cih
where	nr_sequencia = nr_sequencia_p;

if (cd_medico_w = cd_medico_p) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
