CREATE OR REPLACE FUNCTION obter_per_consulta_preco ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_convenio_p bigint, ie_opcao_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE

pr_retorno_w		double precision;
pr_procedimento_w	double precision;
C01 CURSOR FOR
	SELECT	dividir(coalesce(sum(pr_particular),0),count(*))
	from	w_consulta_preco_proc a
	where	a.nm_usuario		= nm_usuario_p
	and	pr_particular		<> 0
	and	a.cd_convenio		= cd_convenio_p;

BEGIN

if (ie_opcao_p		= 1) then

	select	dividir(coalesce(sum(pr_particular),0),count(*))
	into STRICT	pr_retorno_w
	from	w_consulta_preco_proc a
	where	a.nm_usuario		= nm_usuario_p
	and	a.cd_procedimento	= cd_procedimento_p
	and	a.ie_origem_proced	= ie_origem_proced_p
	and	pr_particular		<> 0
	and	a.cd_convenio		= cd_convenio_p;

elsif (ie_opcao_p		= 2) then

	select	dividir(coalesce(sum(pr_particular),0),count(*))
	into STRICT	pr_retorno_w
	from	w_consulta_preco_proc a
	where	a.nm_usuario		= nm_usuario_p
	and	pr_particular		<> 0
	and	a.cd_convenio		= cd_convenio_p;
end if;
return	pr_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
